import React, { Component, createContext } from 'react';
import axios from "axios";
import history from "../../history";
// Importing toastify module 
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
// toast-configuration method,  
// it is compulsory method. 
toast.configure();

// context
const AdminContext = createContext();

// provider
class AdminProvider extends Component {
    state = {
        url: "http://excodeteam.com/crs/api/v1",
        dashboard: {},
        jobs: [],
        singleJob: "",
        jobStatus: "",
        employers: [],
        employerActive: "",
        singleEmployer: {},
        employees: [],
        proposals: [],
        categories: [],
        subCategories: [],
        EmployeeProfile: {
            info: {},
            proposals: {}
        },
        EmployerProfile: {
            info: {},
            jobs: {}
        },
        loading: false,
    }

    authHeader = () => {
        const user = JSON.parse(localStorage.getItem('user'));
        if (user && user.token) {
            return { Authorization: `Bearer ${user.token}` };
        } else {
            // redirect to login page
            return {};
        }
    }

    userLogout = () => {
        localStorage.removeItem("user");
        window.open('/login', '_self');
    };

    getDashboard = async () => {
        this.setState({ loading: true });
        await axios({
            method: 'post',
            url: `${this.state.url}/admin/home`,
            headers: this.authHeader(),
        }).then(
            res => {
                const { data } = res.data;
                const dashboard = data;
                this.setState({ dashboard })
                console.log(res);
            },
            error => {
                console.log(error);
            }
        )
        this.setState({ loading: false });
    }


    getJobs = async (status) => {
        this.setState({ loading: true });
        if (status === "0") {
            status = JSON.stringify(status);
        }
        await axios({
            method: 'post',
            url: `${this.state.url}/admin/all_jobs`,
            headers: this.authHeader(),
            data: { status },
        }).then(
            res => {
                const { data } = res.data;
                const jobs = data;
                this.setState({ jobs })
                console.log(res);
            },
            error => {
                console.log(error);
            }
        )
        this.setState({ loading: false });
    }

    getSingleJob = async (job_id) => {
        this.setState({ loading: true, jobStatus: "" });
        await axios({
            method: 'post',
            url: `${this.state.url}/admin/get_job`,
            headers: this.authHeader(),
            data: { job_id },
        }).then(
            res => {
                const { data } = res.data;
                const singleJob = data;
                this.setState({ singleJob })
                console.log(res);
            },
            error => {
                console.log(error);
            }
        )
        this.setState({ loading: false });
    }

    completeJob = async (id) => {
        console.log("Complete Job", id);
        await axios({
            method: 'post',
            url: `${this.state.url}/admin/complete_job`,
            headers: this.authHeader(),
            data: { "job_id": id },
        }).then(
            res => {
                const { data, job_status } = res.data;
                this.setState({ jobStatus: job_status });
                toast.success('Success Complete Job', { autoClose: 2000, position: toast.POSITION.TOP_CENTER });
                console.log(res);
            },
            error => {
                console.log(error);
            }
        )
    }

    deleteJob = async (id, status) => {
        await axios({
            method: 'post',
            url: `${this.state.url}/employer/delete_job`,
            headers: this.authHeader(),
            data: { "job_id": id },
        }).then(
            res => {
                // const { data } = res.data;
                toast.success('Success Delete Job', { autoClose: 2000, position: toast.POSITION.TOP_CENTER });
            },
            error => {
                console.log(error);
            }
        );
        this.getJobs(status);
    }

    getEmployers = async () => {
        this.setState({ loading: true });
        await axios({
            method: 'post',
            url: `${this.state.url}/admin/get_employers`,
            headers: this.authHeader(),
        }).then(
            res => {
                const { data } = res.data;
                const employers = data;
                this.setState({ employers })
                console.log(res);
            },
            error => {
                console.log(error);
            }
        )
        this.setState({ loading: false });
    }

    activateEmployer = async (id) => {
        await axios({
            method: 'post',
            url: `${this.state.url}/admin/activate_user`,
            headers: this.authHeader(),
            data: { "user_id": id },
        }).then(
            res => {
                const { data, active } = res.data;
                this.setState({
                    employers: data,
                    employerActive: active
                });
                toast.success('Success Activate Employer', { autoClose: 2000, position: toast.POSITION.TOP_CENTER });
                console.log(res);
            },
            error => {
                console.log(error);
            }
        )
    }

    deactivateEmployer = async (id) => {
        await axios({
            method: 'post',
            url: `${this.state.url}/admin/deactivate_user`,
            headers: this.authHeader(),
            data: { "user_id": id },
        }).then(
            res => {
                const { data, active } = res.data;
                this.setState({
                    employers: data,
                    employerActive: active
                });
                toast.success('Success Deactivate_user Employer', { autoClose: 2000, position: toast.POSITION.TOP_CENTER });
                console.log(res);
            },
            error => {
                console.log(error);
            }
        )
    }

    getEmployerJobs = async (employer_id) => {
        this.setState({ loading: true });
        await axios({
            method: 'post',
            url: `${this.state.url}/admin/get_employers`,
            headers: this.authHeader(),
            data: { employer_id },
        }).then(
            res => {
                const { data, jobs } = res.data;
                const employerInfo = data;
                this.setState({
                    singleEmployer: {
                        employerInfo,
                        jobs
                    }
                })
                console.log(res);
            },
            error => {
                console.log(error);
            }
        )
        this.setState({ loading: false });
    }


    getEmployees = async () => {
        this.setState({ loading: true });
        await axios({
            method: 'post',
            url: `${this.state.url}/admin/get_employees`,
            headers: this.authHeader(),
        }).then(
            res => {
                const { data } = res.data;
                const employees = data;
                this.setState({ employees })
                console.log(res);
            },
            error => {
                console.log(error);
            }
        )
        this.setState({ loading: false });
    }

    getProposalsJob = async () => {
        this.setState({ loading: true });
        await axios({
